$(document).ready(function () {
    function close_accordion() {
        $(".wwd_heading").removeClass("is-active");
        $(".wwd_text").slideUp(500).removeClass("wwd_text__active");
        $(".wwd_arrow").removeClass("icon-arrow-up").addClass("icon-arrow-down");
    }
    $(".wwd_heading").on("click", function () {
        var status = $(this).hasClass("is-active");
        if (!status)  {
            close_accordion();
            $(this).addClass("is-active");
            $(".wwd_heading.is-active + .wwd_text").slideDown(500).addClass("wwd_text__active");
            $(".wwd_heading.is-active > .wwd_arrow").removeClass("icon-arrow-down").addClass("icon-arrow-up");

        }
    });
    
    $(function() {
        var toggle = $(".toggle-menu");
        var menu = $(".menu");
        var menuHeight = menu.height();
        
        $(toggle).on("click", function() {
            menu.slideToggle();
        });
        
        $(window).resize(function() {
            var w = $(window).width();
            if (w > 768) {
                menu.removeAttr("style");
            }
        });
    });
});
